data Op = Plus
        | Minus
        | Times
        | Power
        | Mod
        | Cons
        | Divide
        deriving (Eq, Show)

strToOp :: String -> Op
strToOp s = case s of "+" -> Plus
                      "-" -> Minus
                      "*" -> Times
                      "/" -> Divide
                      "^" -> Power
                      ":" -> Cons
                      "%" -> Mod

data Token = TId String
           | TInt Int
           | TOp Op
           | TRP
           | TLP
           | TLB
           | TRB
           | TArr
           | TApp
           | TLet
           | TDef 
           | TRec
           deriving (Eq, Show)

type Tokens = [Token]

tokenize :: String -> Tokens
tokenize = alexScanTokens

alex_action_0 =  \s -> TLP 
