alex_action_2 =  \s -> TLB 
alex_action_3 =  \s -> TRB 
alex_action_4 =  \s -> TLet 
alex_action_5 =  \s -> TArr 
alex_action_6 =  \s -> TOp $ strToOp s 
alex_action_7 =  \s -> TDef 
alex_action_8 =  \s -> TRec 
alex_action_9 =  \s -> TInt $ read s 
alex_action_10 =  \s -> TId s 
